import XCTVapor
@testable import SBVapor

final class AppStoreTests: XCTestCase {
    
    private let receiptString = ""
    
    func testReceiptVerification() throws {
        let app = Application(.testing)
        defer { app.shutdown() }
        app.appStore.initialize()
        try app.register(collection: RouteController())
        
        var headers = HTTPHeaders()
        headers.add(name: .contentType, value: "application/json")
        let receiptRequest = ReceiptRequest(receipt: receiptString)
        
        try app.testable().test(.POST, "app_store", headers: headers, body: nil) { req in
            try req.content.encode(receiptRequest)
        } afterResponse: { res in
            let body = try res.content.decode(AppStoreReceipts.ResponseBody.self)
            XCTAssertEqual(body.environment, .sandbox)
            XCTAssertEqual(body.receipt?.receiptType, .productionSandbox)
            XCTAssertEqual(body.status, .valid)
            XCTAssertEqual(body.latestReceiptInfo.first?.isTrialPeriod, false)
            XCTAssertEqual(body.latestReceiptInfo.first?.isInIntroOfferPeriod, false)
            XCTAssertEqual(body.pendingRenewalInfo.first?.autoRenewStatus, .customerDisabled)
            XCTAssertEqual(body.pendingRenewalInfo.first?.expirationIntent, .customerVoluntarilyCancelled)
            print("\n\n")
            print(body.prettyPrinted)
            print("\n\n")
        }
    }
}


fileprivate extension AppStoreTests {
    
    struct RouteController: RouteCollection {
        
        func boot(routes: RoutesBuilder) throws {
            routes.post("app_store", use: verifyReceipt)
        }
        
        func verifyReceipt(req: Request) throws -> EventLoopFuture<AppStoreReceipts.ResponseBody> {
            let receiptRequest = try req.content.decode(ReceiptRequest.self)
            let receiptData = Data(base64Encoded: receiptRequest.receipt)!
            return req.application.appStore.client.verifyReceipt(receiptData: receiptData, on: req)
        }
    }
    
    struct ReceiptRequest: Content {
        let receipt: String
    }
}
